pragma solidity ^0.4.2;


contract VotingMachine{

  // store Admin
  address Admin;
  // Store Candidates Count
  uint public candidatesCount;
  // Store Voters Count
  uint public votersCount;

  //check Admin rights
	modifier onlyAdmin(){
	  require(msg.sender==Admin);
	  _;
	}

   //check Voter rights
	modifier onlyVoter(){
	  require(voterList[msg.sender].isValue);
	  _;
	}

  //structure to store candidate's details
  struct Candidate{
    uint candidateID;
    string name;
    uint voteCount;
  }

  //structure to store voter's details
  struct Voter{
    uint voterID;
    string name;
   }

  //structure to store voterData
  struct VoterData{
    Voter v;
    bool isValue;
  }


  //structure to store recordData
  struct recordData{
    uint cid;
    bool isValue;
  }

  // mapping to store candidates that have registered
  mapping( uint => Candidate ) public candidates;
  // mapping to store voters that have registered
  mapping( address => VoterData ) public voterList;
  // mapping to store voters that have voted
  mapping( uint => bool ) public voters;

  //mapping to store votes
  mapping ( uint => recordData ) public record;

  //constructor make Admin
  function VotingMachine () public {
    Admin = 0xaaB88Fb14AF312d509793A4c21b93556eA189B21;
  }

  //add candidates                only Admin
  function addCandidate (string _name) onlyAdmin {
      candidates[++candidatesCount] = Candidate(candidatesCount, _name, 0);
  }

  //add voters                    only Admin
  function addVoter (string _name , address _addr) onlyAdmin {
      voterList[_addr] = VoterData(Voter(votersCount, _name),true);
      votersCount++;
  }

  //generate a secure hash
  function hashPrivateKey(bytes _privateKey) returns (uint){
    return uint(keccak256(_privateKey));
  }

  //vote using private key        only Voter
  function vote (bytes _pk, uint _candidateId) public onlyVoter() {

      // require that they haven't voted before
      require(!voters[voterList[msg.sender].v.voterID]);

      // require a valid candidate
      require(_candidateId > 0 && _candidateId <= candidatesCount);

      // update candidate vote Count
      candidates[_candidateId].voteCount ++;

      // record that voter has voted
      voters[voterList[msg.sender].v.voterID] = true;

      //update record
      record[hashPrivateKey(_pk)]= recordData(_candidateId,true);

  }

  // check vote
  function checkVote(bytes _pk) returns (uint){
  if (record[hashPrivateKey(_pk)].isValue) return record[hashPrivateKey(_pk)].cid;
  else return 0;
  }

  //check voters details          only Admin and Voter

